import { useContext } from "react";
import AppContext from "../context/AppContext/AppContext";
import style from "../style/summary.module.css";

const SummaryProduct = ()=>{

    const {cartItems,removeProductFromCart,handleQuantityChange} = useContext(AppContext);
    // console.log(cartItems);

    let totalQty = cartItems.reduce((total, item) => Number(item.quantity) + total, 0);

    let totalPrice = cartItems.reduce((total, item) => Number(item.price)*Number(item.quantity) + total, 0)

    // let price = totalPrice *totalQty;
    totalPrice = Math.trunc(totalPrice);

    return(
        <>
        <h1>Summary Of the Product</h1>
        {cartItems.map((product)=>{
            return <div className='flex space-x-5 border border-gray-200 mt-3 p-3'>
        <div>
            <img alt={product.title} src={product.image} className="h-16 w-16"/>
        </div>
        <div className='w-full'>
                <h2>{product.title}</h2>
                <input type="number" value={product.quantity} onChange={(e)=>{handleQuantityChange(product.id, e.target.value)}} />
                <span className="pl-5">
                  {product.quantity} * {product.price} = {Number(product.quantity)*Number(product.price)}
                </span>
                
                <span>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke-width="1.5"
              stroke="currentColor"
              className="w-6 h-6 text-red-500"
                onClick={()=>{removeProductFromCart(product)}}
            >
              <path
                stroke-linecap="round"
                stroke-linejoin="round"
                d="M14.74 9l-.346 9m-4.788 0L9.26 9m9.968-3.21c.342.052.682.107 1.022.166m-1.022-.165L18.16 19.673a2.25 2.25 0 01-2.244 2.077H8.084a2.25 2.25 0 01-2.244-2.077L4.772 5.79m14.456 0a48.108 48.108 0 00-3.478-.397m-12 .562c.34-.059.68-.114 1.022-.165m0 0a48.11 48.11 0 013.478-.397m7.5 0v-.916c0-1.18-.91-2.164-2.09-2.201a51.964 51.964 0 00-3.32 0c-1.18.037-2.09 1.022-2.09 2.201v.916m7.5 0a48.667 48.667 0 00-7.5 0"
              />
            </svg>
          </span>
        </div>
    </div>
        })}
        {/* <div className={style.outerDiv}> */}
        <hr/>
            <h2 className="text-center text-3xl">
                Total
            </h2>
            <div className={style.innerDiv}>
                <div className={style.divOne}>
              <span>Quantity</span>
              <span>{totalQty}</span>
            </div>
            <div className={style.divTwo}>
              <span>Price</span>
              <span>{totalPrice}</span>
            </div>
            </div>
            
        {/* </div> */}
        </>
    )
};

export default SummaryProduct;
